function [Jt_RLeg, Jt_LLeg, Jt_RArm, Jt_LArm] = jacobians (L,q)
    %
    % Recibe (L,q) las dimensiones del robot y los valores articulares
    %
    % Regresa los jacobianos de todas las extremidades del robot humanoide 
    % respetando el siguiente orden:
    %
    % Jt_RLeg [6x6] : jacobiana total pierna derecha
    % Jt_LLeg [6x6] : jacobiana total pierna izquierda
    % Jt_RArm [3x3] : jacobiana de posicion brazo derecho
    % Jt_LArm [3x3] : jacobiana de posicion brazo izquiedo
    
    % parametros del robot
    L1 = L(1); L2 = L(2); L3 = L(3); L4 = L(4);
    D1 = L(5); D2 = L(6); D3 = L(7); D4 = L(8);

    % valores articulares
    q1 = q(1); q2 = q(2); q3 = q(3); q4 = q(4); q5 = q(5); q6 = q(6);
    q7 = q(7); q9 = q(9); q11 = q(11); q13 = q(13); q15 = q(15); q17 = q(17);
    q8 = q(8); q10 = q(10); q12 = q(12); q14 = q(14); q16 = q(16); q18 = q(18);

    % jacobiano pierna derecha ------------------------
    
    Jt_RLeg = [... %renglon 1
            - sin(q7)*(L4*sin(q11 + q13) + L3*sin(q11)) - cos(q7)*sin(q9)*(L4*cos(q11 + q13) + L3*cos(q11)), ...
            -cos(q9)*sin(q7)*(L4*cos(q11 + q13) + L3*cos(q11)), ...
            cos(q7)*(L4*cos(q11 + q13) + L3*cos(q11)) + sin(q7)*sin(q9)*(L4*sin(q11 + q13) + L3*sin(q11)),...
            L4*cos(q11 + q13)*cos(q7) + L4*sin(q11 + q13)*sin(q7)*sin(q9),...
            0,...
            0;...
            ... % renglon 2
            sin(q7)*sin(q9)*(L4*cos(q11 + q13) + L3*cos(q11)) - cos(q7)*(L4*sin(q11 + q13) + L3*sin(q11)),...
            -cos(q7)*cos(q9)*(L4*cos(q11 + q13) + L3*cos(q11)),...
            cos(q7)*sin(q9)*(L4*sin(q11 + q13) + L3*sin(q11)) - sin(q7)*(L4*cos(q11 + q13) + L3*cos(q11)),...
            L4*sin(q11 + q13)*cos(q7)*sin(q9) - L4*cos(q11 + q13)*sin(q7),...
            0, ...
            0; ...
            ... % renglon 3
            0, ...
            sin(q9)*(L4*cos(q11 + q13) + L3*cos(q11)), ...
            cos(q9)*(L4*sin(q11 + q13) + L3*sin(q11)), ...
            L4*sin(q11 + q13)*cos(q9), ...
            0, ...
            0; ...
            ... % renglon 4
            0, ...
            (cos(q9)*sin(q17) - cos(q11 + q13 - q15)*cos(q17)*sin(q9))*(cos(q17)*(sin(q11 + q13 - q15)*sin(q7) + cos(q11 + q13 - q15)*cos(q7)*sin(q9)) - cos(q7)*cos(q9)*sin(q17)) - (cos(q9)*cos(q17) + cos(q11 + q13 - q15)*sin(q9)*sin(q17))*(sin(q17)*(sin(q11 + q13 - q15)*sin(q7) + cos(q11 + q13 - q15)*cos(q7)*sin(q9)) + cos(q7)*cos(q9)*cos(q17)) + sin(q11 + q13 - q15)*sin(q9)*(cos(q11 + q13 - q15)*sin(q7) - sin(q11 + q13 - q15)*cos(q7)*sin(q9)), ...
            -cos(q9)*sin(q7), ...
            -cos(q9)*sin(q7), ...
            cos(q9)*sin(q7), ...
            cos(q11 + q13 - q15)*cos(q7) + sin(q11 + q13 - q15)*sin(q7)*sin(q9); ...
            ... % renglon 5
            (sin(q9)*sin(q17) + cos(q11 + q13 - q15)*cos(q9)*cos(q17))*(cos(q17)*(sin(q11 + q13 - q15)*sin(q7) + cos(q11 + q13 - q15)*cos(q7)*sin(q9)) - cos(q7)*cos(q9)*sin(q17)) - (cos(q17)*sin(q9) - cos(q11 + q13 - q15)*cos(q9)*sin(q17))*(sin(q17)*(sin(q11 + q13 - q15)*sin(q7) + cos(q11 + q13 - q15)*cos(q7)*sin(q9)) + cos(q7)*cos(q9)*cos(q17)) - sin(q11 + q13 - q15)*cos(q9)*(cos(q11 + q13 - q15)*sin(q7) - sin(q11 + q13 - q15)*cos(q7)*sin(q9)), ...
            sin(q7), ...
            sin(q17)*(cos(q17)*sin(q9) - cos(q11 + q13 - q15)*cos(q9)*sin(q17))*(cos(q11 + q13 - q15)*cos(q7) + sin(q11 + q13 - q15)*sin(q7)*sin(q9)) - cos(q17)*(sin(q9)*sin(q17) + cos(q11 + q13 - q15)*cos(q9)*cos(q17))*(cos(q11 + q13 - q15)*cos(q7) + sin(q11 + q13 - q15)*sin(q7)*sin(q9)) - sin(q11 + q13 - q15)*cos(q9)*(sin(q11 + q13 - q15)*cos(q7) - cos(q11 + q13 - q15)*sin(q7)*sin(q9)), ...
            sin(q17)*(cos(q17)*sin(q9) - cos(q11 + q13 - q15)*cos(q9)*sin(q17))*(cos(q11 + q13 - q15)*cos(q7) + sin(q11 + q13 - q15)*sin(q7)*sin(q9)) - cos(q17)*(sin(q9)*sin(q17) + cos(q11 + q13 - q15)*cos(q9)*cos(q17))*(cos(q11 + q13 - q15)*cos(q7) + sin(q11 + q13 - q15)*sin(q7)*sin(q9)) - sin(q11 + q13 - q15)*cos(q9)*(sin(q11 + q13 - q15)*cos(q7) - cos(q11 + q13 - q15)*sin(q7)*sin(q9)), ...
            cos(q7)*cos(q9), ...
            sin(q11 + q13 - q15)*cos(q7)*sin(q9) - cos(q11 + q13 - q15)*sin(q7); ...
            ... %renglon 6
            - (cos(q11 + q13 - q15)*cos(q7) + sin(q11 + q13 - q15)*sin(q7)*sin(q9))^2 - (cos(q17)*(sin(q11 + q13 - q15)*cos(q7) - cos(q11 + q13 - q15)*sin(q7)*sin(q9)) + cos(q9)*sin(q7)*sin(q17))^2 - (sin(q17)*(sin(q11 + q13 - q15)*cos(q7) - cos(q11 + q13 - q15)*sin(q7)*sin(q9)) - cos(q9)*cos(q17)*sin(q7))^2, ...
            (sin(q17)*(sin(q11 + q13 - q15)*cos(q7) - cos(q11 + q13 - q15)*sin(q7)*sin(q9)) - cos(q9)*cos(q17)*sin(q7))*(cos(q7)*cos(q17)*sin(q9) - cos(q11 + q13 - q15)*cos(q7)*cos(q9)*sin(q17)) - (cos(q17)*(sin(q11 + q13 - q15)*cos(q7) - cos(q11 + q13 - q15)*sin(q7)*sin(q9)) + cos(q9)*sin(q7)*sin(q17))*(cos(q7)*sin(q9)*sin(q17) + cos(q11 + q13 - q15)*cos(q7)*cos(q9)*cos(q17)) + sin(q11 + q13 - q15)*cos(q7)*cos(q9)*(cos(q11 + q13 - q15)*cos(q7) + sin(q11 + q13 - q15)*sin(q7)*sin(q9)), ...
            (cos(q11 + q13 - q15)*cos(q7) + sin(q11 + q13 - q15)*sin(q7)*sin(q9))*(sin(q11 + q13 - q15)*sin(q7) + cos(q11 + q13 - q15)*cos(q7)*sin(q9)) - cos(q17)*(cos(q17)*(sin(q11 + q13 - q15)*cos(q7) - cos(q11 + q13 - q15)*sin(q7)*sin(q9)) + cos(q9)*sin(q7)*sin(q17))*(cos(q11 + q13 - q15)*sin(q7) - sin(q11 + q13 - q15)*cos(q7)*sin(q9)) - sin(q17)*(sin(q17)*(sin(q11 + q13 - q15)*cos(q7) - cos(q11 + q13 - q15)*sin(q7)*sin(q9)) - cos(q9)*cos(q17)*sin(q7))*(cos(q11 + q13 - q15)*sin(q7) - sin(q11 + q13 - q15)*cos(q7)*sin(q9)), ...
            (cos(q11 + q13 - q15)*cos(q7) + sin(q11 + q13 - q15)*sin(q7)*sin(q9))*(sin(q11 + q13 - q15)*sin(q7) + cos(q11 + q13 - q15)*cos(q7)*sin(q9)) - cos(q17)*(cos(q17)*(sin(q11 + q13 - q15)*cos(q7) - cos(q11 + q13 - q15)*sin(q7)*sin(q9)) + cos(q9)*sin(q7)*sin(q17))*(cos(q11 + q13 - q15)*sin(q7) - sin(q11 + q13 - q15)*cos(q7)*sin(q9)) - sin(q17)*(sin(q17)*(sin(q11 + q13 - q15)*cos(q7) - cos(q11 + q13 - q15)*sin(q7)*sin(q9)) - cos(q9)*cos(q17)*sin(q7))*(cos(q11 + q13 - q15)*sin(q7) - sin(q11 + q13 - q15)*cos(q7)*sin(q9)), ...
            cos(q17)*(cos(q17)*(sin(q11 + q13 - q15)*cos(q7) - cos(q11 + q13 - q15)*sin(q7)*sin(q9)) + cos(q9)*sin(q7)*sin(q17))*(cos(q11 + q13 - q15)*sin(q7) - sin(q11 + q13 - q15)*cos(q7)*sin(q9)) - (cos(q11 + q13 - q15)*cos(q7) + sin(q11 + q13 - q15)*sin(q7)*sin(q9))*(sin(q11 + q13 - q15)*sin(q7) + cos(q11 + q13 - q15)*cos(q7)*sin(q9)) + sin(q17)*(sin(q17)*(sin(q11 + q13 - q15)*cos(q7) - cos(q11 + q13 - q15)*sin(q7)*sin(q9)) - cos(q9)*cos(q17)*sin(q7))*(cos(q11 + q13 - q15)*sin(q7) - sin(q11 + q13 - q15)*cos(q7)*sin(q9)), ...
            sin(q11 + q13 - q15)*cos(q9)];

    % jacobiano pierna izquierda ------------------------
    Jt_LLeg = [... % renglon 1
            sin(q8)*(L4*sin(q12 + q14) + L3*sin(q12)) - cos(q8)*sin(q10)*(L4*cos(q12 + q14) + L3*cos(q12)), ...
            -cos(q10)*sin(q8)*(L4*cos(q12 + q14) + L3*cos(q12)), ...
            sin(q8)*sin(q10)*(L4*sin(q12 + q14) + L3*sin(q12)) - cos(q8)*(L4*cos(q12 + q14) + L3*cos(q12)), ...
            L4*sin(q12 + q14)*sin(q8)*sin(q10) - L4*cos(q12 + q14)*cos(q8), ...
            0, ...
            0; ...
            ... % renglon 2
            cos(q8)*(L4*sin(q12 + q14) + L3*sin(q12)) + sin(q8)*sin(q10)*(L4*cos(q12 + q14) + L3*cos(q12)), ...
            -cos(q8)*cos(q10)*(L4*cos(q12 + q14) + L3*cos(q12)), ...
            sin(q8)*(L4*cos(q12 + q14) + L3*cos(q12)) + cos(q8)*sin(q10)*(L4*sin(q12 + q14) + L3*sin(q12)), ...
            L4*cos(q12 + q14)*sin(q8) + L4*sin(q12 + q14)*cos(q8)*sin(q10), ...
            0, ...
            0; ...
            ... % renglon 3
            0, ...
            sin(q10)*(L4*cos(q12 + q14) + L3*cos(q12)), ...
            cos(q10)*(L4*sin(q12 + q14) + L3*sin(q12)), ...
            L4*sin(q12 + q14)*cos(q10), ...
            0, ...
            0; ...
            ... % renglon 4
            0, ...
            (cos(q10)*cos(q18) + cos(q12 + q14 - q16)*sin(q10)*sin(q18))*(sin(q18)*(sin(q12 + q14 - q16)*sin(q8) - cos(q12 + q14 - q16)*cos(q8)*sin(q10)) - cos(q8)*cos(q10)*cos(q18)) - (cos(q10)*sin(q18) - cos(q12 + q14 - q16)*cos(q18)*sin(q10))*(cos(q18)*(sin(q12 + q14 - q16)*sin(q8) - cos(q12 + q14 - q16)*cos(q8)*sin(q10)) + cos(q8)*cos(q10)*sin(q18)) - sin(q12 + q14 - q16)*sin(q10)*(cos(q12 + q14 - q16)*sin(q8) + sin(q12 + q14 - q16)*cos(q8)*sin(q10)), ...
            cos(q10)*sin(q8), ...
            cos(q10)*sin(q8), ...
            -cos(q10)*sin(q8), ...
            cos(q12 + q14 - q16)*cos(q8) - sin(q12 + q14 - q16)*sin(q8)*sin(q10); ...
            ... % renglon 5
            (cos(q18)*sin(q10) - cos(q12 + q14 - q16)*cos(q10)*sin(q18))*(sin(q18)*(sin(q12 + q14 - q16)*sin(q8) - cos(q12 + q14 - q16)*cos(q8)*sin(q10)) - cos(q8)*cos(q10)*cos(q18)) - (sin(q10)*sin(q18) + cos(q12 + q14 - q16)*cos(q10)*cos(q18))*(cos(q18)*(sin(q12 + q14 - q16)*sin(q8) - cos(q12 + q14 - q16)*cos(q8)*sin(q10)) + cos(q8)*cos(q10)*sin(q18)) + sin(q12 + q14 - q16)*cos(q10)*(cos(q12 + q14 - q16)*sin(q8) + sin(q12 + q14 - q16)*cos(q8)*sin(q10)), ...
            sin(q8), ...
            cos(q8)*cos(q10), ...
            cos(q8)*cos(q10), ...
            -cos(q8)*cos(q10), ...
            - cos(q12 + q14 - q16)*sin(q8) - sin(q12 + q14 - q16)*cos(q8)*sin(q10); ...
            ... % renglon 6
            - (cos(q12 + q14 - q16)*cos(q8) - sin(q12 + q14 - q16)*sin(q8)*sin(q10))^2 - (sin(q18)*(sin(q12 + q14 - q16)*cos(q8) + cos(q12 + q14 - q16)*sin(q8)*sin(q10)) + cos(q10)*cos(q18)*sin(q8))^2 - (cos(q18)*(sin(q12 + q14 - q16)*cos(q8) + cos(q12 + q14 - q16)*sin(q8)*sin(q10)) - cos(q10)*sin(q8)*sin(q18))^2, ...
            (cos(q18)*(sin(q12 + q14 - q16)*cos(q8) + cos(q12 + q14 - q16)*sin(q8)*sin(q10)) - cos(q10)*sin(q8)*sin(q18))*(cos(q8)*sin(q10)*sin(q18) + cos(q12 + q14 - q16)*cos(q8)*cos(q10)*cos(q18)) - (sin(q18)*(sin(q12 + q14 - q16)*cos(q8) + cos(q12 + q14 - q16)*sin(q8)*sin(q10)) + cos(q10)*cos(q18)*sin(q8))*(cos(q8)*cos(q18)*sin(q10) - cos(q12 + q14 - q16)*cos(q8)*cos(q10)*sin(q18)) - sin(q12 + q14 - q16)*cos(q8)*cos(q10)*(cos(q12 + q14 - q16)*cos(q8) - sin(q12 + q14 - q16)*sin(q8)*sin(q10)), ...
            (cos(q12 + q14 - q16)*cos(q8) - sin(q12 + q14 - q16)*sin(q8)*sin(q10))*(sin(q12 + q14 - q16)*sin(q8) - cos(q12 + q14 - q16)*cos(q8)*sin(q10)) - cos(q18)*(cos(q18)*(sin(q12 + q14 - q16)*cos(q8) + cos(q12 + q14 - q16)*sin(q8)*sin(q10)) - cos(q10)*sin(q8)*sin(q18))*(cos(q12 + q14 - q16)*sin(q8) + sin(q12 + q14 - q16)*cos(q8)*sin(q10)) - sin(q18)*(sin(q18)*(sin(q12 + q14 - q16)*cos(q8) + cos(q12 + q14 - q16)*sin(q8)*sin(q10)) + cos(q10)*cos(q18)*sin(q8))*(cos(q12 + q14 - q16)*sin(q8) + sin(q12 + q14 - q16)*cos(q8)*sin(q10)), ...
            (cos(q12 + q14 - q16)*cos(q8) - sin(q12 + q14 - q16)*sin(q8)*sin(q10))*(sin(q12 + q14 - q16)*sin(q8) - cos(q12 + q14 - q16)*cos(q8)*sin(q10)) - cos(q18)*(cos(q18)*(sin(q12 + q14 - q16)*cos(q8) + cos(q12 + q14 - q16)*sin(q8)*sin(q10)) - cos(q10)*sin(q8)*sin(q18))*(cos(q12 + q14 - q16)*sin(q8) + sin(q12 + q14 - q16)*cos(q8)*sin(q10)) - sin(q18)*(sin(q18)*(sin(q12 + q14 - q16)*cos(q8) + cos(q12 + q14 - q16)*sin(q8)*sin(q10)) + cos(q10)*cos(q18)*sin(q8))*(cos(q12 + q14 - q16)*sin(q8) + sin(q12 + q14 - q16)*cos(q8)*sin(q10)), ...
            cos(q18)*(cos(q18)*(sin(q12 + q14 - q16)*cos(q8) + cos(q12 + q14 - q16)*sin(q8)*sin(q10)) - cos(q10)*sin(q8)*sin(q18))*(cos(q12 + q14 - q16)*sin(q8) + sin(q12 + q14 - q16)*cos(q8)*sin(q10)) - (cos(q12 + q14 - q16)*cos(q8) - sin(q12 + q14 - q16)*sin(q8)*sin(q10))*(sin(q12 + q14 - q16)*sin(q8) - cos(q12 + q14 - q16)*cos(q8)*sin(q10)) + sin(q18)*(sin(q18)*(sin(q12 + q14 - q16)*cos(q8) + cos(q12 + q14 - q16)*sin(q8)*sin(q10)) + cos(q10)*cos(q18)*sin(q8))*(cos(q12 + q14 - q16)*sin(q8) + sin(q12 + q14 - q16)*cos(q8)*sin(q10)), ...
            -sin(q12 + q14 - q16)*cos(q10)];
   
    % jacobiano brazo derecho ------------------------
    Jt_RArm = [cos(q1)*(D3*cos(q3) + D4*cos(q3 - q5)), -sin(q1)*(D3*sin(q3) + D4*sin(q3 - q5)),  D4*sin(q3 - q5)*sin(q1); ...
               0, - D3*cos(q3) - D4*cos(q3 - q5), D4*cos(q3 - q5); ...
               sin(q1)*(D3*cos(q3) + D4*cos(q3 - q5)),  cos(q1)*(D3*sin(q3) + D4*sin(q3 - q5)), -D4*sin(q3 - q5)*cos(q1)];

    % jacobiano brazo izquierdo ------------------------
    Jt_LArm = [-cos(q2)*(D3*cos(q4) + D4*cos(q4 - q6)), sin(q2)*(D3*sin(q4) + D4*sin(q4 - q6)), -D4*sin(q4 - q6)*sin(q2); ...
              0, - D3*cos(q4) - D4*cos(q4 - q6), D4*cos(q4 - q6); ...
              sin(q2)*(D3*cos(q4) + D4*cos(q4 - q6)), cos(q2)*(D3*sin(q4) + D4*sin(q4 - q6)), -D4*sin(q4 - q6)*cos(q2)];
end